/* eslint-disable no-unused-expressions */
import "ethereum-waffle";
import { Address } from "soltypes";
import { Block, serializeFinalization, headerHash } from "~core";
import { parseEther } from "ethers/lib/utils";
import { SignerWithAddress } from "@nomiclabs/hardhat-ethers/signers";
import { CoordinatableTester } from "../../typechain";
import { Context, ZkopruTestFixture } from "../fixtures";

const rawData =
  "";
const block = Block.from(rawData);
const [tx1In2Out, tx2In2Out, tx2In3Out] = block.body.txs;

describe("coordinator functions gas report", async () => {
  let fixtures: ZkopruTestFixture;
  let ct: CoordinatableTester;
  let account: SignerWithAddress;
  const context = new Context();
  beforeEach(async () => {
    fixtures = await context.getFixtures();
    account = await context.getDeployer();
    ct = fixtures.coordinatableTester;
    await ct.connect(account).stake(account.address, {
      value: parseEther("32")
    });
  });
  describe("propose(): (1 input - 2 output)", () => {
    it("10 tx", async () => {
      const block = Block.from(rawData);
      block.body.txs = Array(10)
        .fill(undefined)
        .reduce((acc, _) => [...acc, tx1In2Out], []);
      block.header.proposer = Address.from(account.address);
      await ct.propose(block.serializeBlock());
    });
    it("110 tx", async () => {
      const block = Block.from(rawData);
      block.body.txs = Array(110)
        .fill(undefined)
        .reduce((acc, _) => [...acc, tx1In2Out], []);
      block.header.proposer = Address.from(account.address);
      await ct.propose(block.serializeBlock());
    });
  });
  describe("propose(): (2 input - 2 output)", () => {
    it("10 tx", async () => {
      const block = Block.from(rawData);
      block.body.txs = Array(10)
        .fill(undefined)
        .reduce((acc, _) => [...acc, tx2In2Out], []);
      block.header.proposer = Address.from(account.address);
      await ct.propose(block.serializeBlock());
    });
    it("110 tx", async () => {
      const block = Block.from(rawData);
      block.body.txs = Array(110)
        .fill(undefined)
        .reduce((acc, _) => [...acc, tx2In2Out], []);
      block.header.proposer = Address.from(account.address);
      await ct.propose(block.serializeBlock());
    });
  });
  describe("propose(): (2 input - 3 output)", () => {
    it("10 tx", async () => {
      const block = Block.from(rawData);
      block.body.txs = Array(10)
        .fill(undefined)
        .reduce((acc, _) => [...acc, tx2In3Out], []);
      block.header.proposer = Address.from(account.address);
      await ct.propose(block.serializeBlock());
    });
    it("110 tx", async () => {
      const block = Block.from(rawData);
      block.body.txs = Array(110)
        .fill(undefined)
        .reduce((acc, _) => [...acc, tx2In3Out], []);
      block.header.proposer = Address.from(account.address);
      await ct.propose(block.serializeBlock());
    });
  });
  describe("finalize()", () => {
    it("finalization", async () => {
      const finalization = Block.from(rawData).getFinalization();
      await ct.mockFinalization(
        finalization.proposalChecksum.toString(),
        headerHash(finalization.header).toString(),
        finalization.header.parentBlock.toString()
      );
      const data = serializeFinalization(finalization);
      await ct.finalize(data);
    });
  });
});
